<?phpclass App {public $appdir='/app';    function general($app_name){        include_once $this->appdir.'/'.$app_name.'/'.strtolower($app_name).'.php';        $view=$app_name::general();        return $view;    }}class App_S {    public static function view($app_name){    $app=new App;    $view=$app->general($app_name);    return $view;}   public static function general(){     $app=new App;        return $app;    }}class Tool extends App {    public $appdir='/app/tool';}class Tool_S {    public static function view($tool_name){        $tool=new Tool;        $view=$tool->view($tool_name);        return $view;    }}class AppTool extends App {    public $appdir='';    function general($app_name,$tool_name){        include_once $this->appdir.'/'.$app_name.'/tool/'.$tool_name.'.php';        $view=$tool_name::general();        return $view;    }}class AppTool_S {    public static function general($tool_name){        $tool=new AppTool;        $view=$tool->general($tool_name);        return $view;    }}