<?phpclass App{//--    public  $appdir = 'app';    public $path ='';    public $appname ='';    function __construct($app_name){        $this->path = $this->appdir.'/'.strtolower($app_name).'/'.strtolower($app_name).'.php';        $this->appname=$app_name;    }    function view($app_name='')    {        if($app_name!=''){ $this->path = $this->appdir.'/'.strtolower($app_name).'/'.strtolower($app_name).'.php';        }else{$app_name= $this->appname;}        include_once $this->path;        $view = $app_name::view();        return $view;    }}class App_S{    public static function view($app_name)    {        $app = new App($app_name);        $view = $app->view();        return $view;    }    public static function general()    {        $app = new App;        return $app;    }}class Tool extends App{    public $appdir = 'app/tool';}class Tool_S{    public static function view($app_name)    {        $app = new Tool($app_name);        $view = $app->view();        return $view;    }}class AppTool extends App{    public $appdir = '';}class AppTool_S{    public static function general($tool_name)    {        $tool = new AppTool;        $view = $tool->general($tool_name);        return $view;    }}